testing.map:
function(doc) {

  var tableLength=doc.data.length;
  var columnWidth=doc.meta.view.columns.length;
  var names = [];

  for(var j=0; j<columnWidth;j++){
    var tmpN=doc.meta.view.columns[j].name
    names.push(tmpN);
  }

  for(var i=0; i<tableLength;i++){
	var column = {}
	for(var j=0; j<columnWidth;j++){
    		var tmpV=doc.data[i][j];
		var tmpN=names[j];
    		column[tmpN]=tmpV;
  	}
	emit(column.id, column);
  }


}
